import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Form, FormControl, Button } from 'react-bootstrap';
import foodImages from './FoodImages';
import './images.css';

import transparentVideo from '../Images/pexels_videos_2832316 (1080p).mp4'; // Import your transparent video file

export default function MainPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');

  const handleImageClick = (type) => {
    if (type === 'biryani') {
      navigate('/biryanis');
    } else if (type === 'pizzas') {
      navigate('/offers');
    } else if (type === 'Tiffins') {
      navigate('/tiffins');
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Perform search logic here, e.g., navigate to search results page
    navigate(`/search?q=${searchTerm}`);
  };

  return (
    <Container>
      <Row>
        <Col>
          <Form inline onSubmit={handleSearch}>
            <FormControl
              type="text"
              placeholder="Search"
              className="mr-sm-2"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Button variant="outline-success" type="submit">Search</Button>
          </Form>
        </Col>
      </Row>
      <Row>
        <div className='image-container'>
          {foodImages.map((foodImage, index) => (
            <div key={index} onClick={() => handleImageClick(foodImage.type)}>
              <div className='circular-image'>
                {foodImage.image}
              </div>
              <h1>{foodImage.type}</h1>
            </div>
          ))}
        </div>
      </Row>
      <video
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: 'fixed',
          bottom: 0,
          right: 0,
          minWidth: '100%',
          minHeight: '100%',
          width: 'auto',
          height: 'auto',
          zIndex: -1000,
          opacity: 0.5, // Set the opacity for transparency
        }}
      >
        <source src={transparentVideo} type='video/webm' />
        Your browser does not support the video tag.
      </video>
    </Container>
  );
}
