import React, { useState } from 'react';
import { useDispatch } from 'react-redux'; // Import the useDispatch hook
 // Import the addtocart action creator
import { Modal } from 'react-bootstrap';
export default function Pizza({ pizza }) {
    const [quantity, setQuantity] = useState(1);
    const [varient, setVarient] = useState('small');
    const [show, setShow] = useState(false);
    const dispatch = useDispatch(); // Get the dispatch function from the Redux store

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const addToCartHandler = () => {
        dispatch(addtocart(pizza, quantity, varient)); // Dispatch the addtocart action with pizza, quantity, and varient
    };

    return (
        <div style={{ margin: "50px" }} className='shadow-lg p-3 mb-5 bg-white rounded'>
            <div onClick={handleShow} style={{ cursor: 'pointer' }}>
                <h1>{pizza.name}</h1>
                <img src={pizza.image} className='img-fluid' alt={pizza.name} style={{ height: "200px", width: "200px" }} />
            </div>

            <div className="flex-container">
                <div className='w-100 m-1'>
                    <p>Variants</p>
                    <select className='form-control' value={varient} onChange={(e) => { setVarient(e.target.value) }}>
                        {pizza.variants.map(variant => (
                            <option key={variant} value={variant}>{variant}</option>
                        ))}
                    </select>
                </div>
                <div className='w-100 m-1'>
                    <p>Quantity</p>
                    <select className='form-control' value={quantity} onChange={(e) => { setQuantity(e.target.value) }}>
                        {[...Array(10).keys()].map((_, i) => (
                            <option key={i} value={i + 1}>{i + 1}</option>
                        ))}
                    </select>
                    <div className="flex-container">
                        <div className='m-1 w-100'>
                            <h1 className='mt-1'>Price: {pizza.prices[0][varient] * quantity} Rs/-</h1>
                        </div>
                        <div className='m-1 w-100'>
                            <button className='btn' onClick={addToCartHandler}>ADD TO CART</button>
                        </div>
                    </div>
                </div>
            </div>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{pizza.name}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <img src={pizza.image} className='img-fluid' style={{ height: "400px" }} />
                    <p>{pizza.description}</p>
                </Modal.Body>
                <Modal.Footer>
                    <button className='btn' onClick={handleClose}>CLOSE</button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}
