import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row } from 'react-bootstrap';
import foodImages from './FoodImages';
import './images.css';

import transparentVideo from '../Images/pexels_videos_2832316 (1080p).mp4';

export default function MainPage() {
  const navigate = useNavigate();
  const [jobseekers, setJobSeekers] = useState([]);

  const handleImageClick = (type) => {
    if (type === 'biryani') {
      navigate('/biryanis');
    } else if (type === 'pizzas') {
      navigate('/offers');
    } else if (type === 'Tiffins') {
      navigate('/tiffins');
    } else if (type === 'Non-veg Soups') {
      navigate('/nonvegsoups');
    }
  };

  const fetchJobSeekers = async () => {
    try {
      const response = await axios.get('http://localhost:2001/viewmenu');
      setJobSeekers(response.data);
    } catch (error) {
      console.error('Error fetching job seekers:', error.message);
    }
  };

  useEffect(() => {
    fetchJobSeekers();
    // Cleanup function to prevent memory leaks
    return () => {
      setJobSeekers([]);
    };
  }, []);

  const cardStyle = {
    width: '200px',
    margin: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '8px',
    overflow: 'hidden',
    transition: 'transform 0.3s',
  };

  const hoverStyle = {
    transform: 'scale(1.05)',
  };

  return (
    <Container>
      <Row>
        <div className='image-container'>
          {foodImages.map((foodImage, index) => (
            <div key={index} onClick={() => handleImageClick(foodImage.type)}>
              <div className='circular-image'>
                {foodImage.image}
              </div>
              <h1>{foodImage.type}</h1>
            </div>
          ))}
        </div>
      </Row>
      <video
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: 'fixed',
          bottom: 0,
          right: 0,
          minWidth: '100%',
          minHeight: '100%',
          width: 'auto',
          height: 'auto',
          zIndex: -1000,
          opacity: 0.5,
        }}>
        <source src={transparentVideo} type='video/webm' />
        Your browser does not support the video tag.
      </video>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {jobseekers.map((jobseeker, index) => (
          <div
            key={index}
            style={{ ...cardStyle, ...(jobseeker.isHovered ? hoverStyle : {}) }}
            onMouseEnter={() => setJobSeekers(prevState => prevState.map((item, i) => (i === index ? { ...item, isHovered: true } : item)))}
            onMouseLeave={() => setJobSeekers(prevState => prevState.map((item, i) => (i === index ? { ...item, isHovered: false } : item)))}>
            {jobseeker.image && (
              <img
                src={`data:${jobseeker.image.contentType};base64,${jobseeker.image.data}`}
                alt=''
                style={{ width: '100%', height: '150px', objectFit: 'cover' }}
              />
            )}
            <div style={{ padding: '10px' }}>
              <h4>{jobseeker.itemname}</h4>
              <p>Price: Rs:{jobseeker.price}/-</p>
              <p>Quantity: {jobseeker.quantity}</p>
              <p>varient: {jobseeker.varient}</p>
            </div>
          </div>
        ))}
      </div>
    </Container>
  );
}
