// Slogin.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link, Route, useNavigate , Routes} from 'react-router-dom';
import Register from './SRegistration';
import '../Scomponents/SellersNavBar'

export default function Slogin() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:2001/checksellerlogin', formData);
      console.log('Login Response:', response.data);

      if (response.data != null) {
        console.log('Login Successful');
        navigate(`/Sellerprofile/${formData.email}`);  // Pass email as a URL parameter
      } else {
        setMessage("Login Failed");
        setError("");
      }
    } catch (error) {
      console.error('Login Error:', error.message);
      setMessage("");
      setError(error.message);
    }
  };
  
const handlesignup=()=>{
  navigate('/sellerregister');
}
  return (
    <div className='card'>
      <h3 align="center"><u>Seller Login</u></h3>
      {
        message ? <h4 align="center">{message}</h4> : <h4 align="center">{error}</h4>
      }
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email</label>
          <input type="email" id="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Password</label>
          <input type="password" id="password" value={formData.password} onChange={handleChange} required />
        </div>
        <button type="submit" className="button">Login</button>
        {/* Updated Link to directly navigate to the registration page */}
        <h4>No Account?<button to="register" className="registerButton" onclick={handlesignup()}>Register</button></h4>
        
      </form>
    </div>
  );
}
