import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function PaymentModeSelect() {
  const [selectedPaymentMode, setSelectedPaymentMode] = useState('');

  const handlePaymentModeChange = (event) => {
    setSelectedPaymentMode(event.target.value);
  };

  return (
    <div>
      <h1>Select Payment Mode</h1>
      <select value={selectedPaymentMode} onChange={handlePaymentModeChange}>
        <option value="">Select Payment Mode</option>
        <option value="cashon">Cash on Delivery</option>
        <option value="card">Card</option>
        <option value="upay">UPI</option>
      </select>
      {selectedPaymentMode && <p>Selected Payment Mode: {selectedPaymentMode}</p>}
    </div>
  );
}

export default function Cart() {
  const [jobseekers, setJobSeekers] = useState([]);
  const navigate = useNavigate();

  const fetchJobSeekers = async () => {
    try {
      const response = await axios.get('http://localhost:2001/viewmenu');
      setJobSeekers(response.data);
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    fetchJobSeekers();
  }, []);

  const cardStyle = {
    width: '200px',
    margin: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '8px',
    overflow: 'hidden',
    transition: 'transform 0.3s',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
  };

  const hoverStyle = {
    transform: 'scale(1.05)',
  };

  return (
    <div>
      <h1>Cart Items</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {jobseekers.map((jobseeker, index) => (
          <div
            key={index}
            style={{ ...cardStyle, ...(jobseeker.isHovered ? hoverStyle : {}) }}
            onMouseEnter={() => setJobSeekers(prevState => prevState.map((item, i) => (i === index ? { ...item, isHovered: true } : item)))}
            onMouseLeave={() => setJobSeekers(prevState => prevState.map((item, i) => (i === index ? { ...item, isHovered: false } : item)))}
          >
            {jobseeker.image && (
              <img
                src={`data:${jobseeker.image.contentType};base64,${jobseeker.image.data}`}
                alt=""
                style={{ width: '100%', height: '150px', objectFit: 'cover' }}
              />
            )}
            <div style={{ padding: '10px' }}>
              <h4>{jobseeker.itemname}</h4>
              <p>Price: Rs:{jobseeker.price}/-</p>
              <p>Varient: {jobseeker.varient}</p>
              <p>Quantity: {jobseeker.quantity}</p>
            </div>
          </div>
        ))}
      </div>
      <PaymentModeSelect />
    </div>
  );
}
