import React, { useState } from 'react';
import { Modal } from 'react-bootstrap'; // Add this import

export default function Pizza({ pizza }) {
    const [quantity, setQuantity] = useState(1);
    const [variant, setVariant] = useState('small');
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <div style={{ margin: "50px" }} className='shadow-lg p-3 mb-5 bg-white rounded'>
           

            <div className="flex-container">
                <div className='w-100 m-1'>
                    <p>Variants</p>
                    <select className='form-control' value={variant} onChange={(e) => { setVariant(e.target.value) }}>
                        {pizza.variants.map(variant => (
                            <option key={variant} value={variant}>{variant}</option>
                        ))}
                    </select>
                </div>
                <div className='w-100 m-1'>
                    <p>Quantity</p>
                    <select className='form-control' value={quantity} onChange={(e) => { setQuantity(e.target.value) }}>
                        {[...Array(10).keys()].map((_, i) => (
                            <option key={i} value={i + 1}>{i + 1}</option>
                        ))}
                    </select>
                    <div className="flex-container">
                        <div className='m-1 w-100'>
                            <h1 className='mt-1'>Price: {pizza.prices[0][variant] * quantity} Rs/-</h1>
                        </div>
                        <div className='m-1 w-100'>
                            <button className='btn' onClick={handleShow}>ADD TO CART</button>
                        </div>
                        <Modal show={show} onHide={handleClose}>
                            <Modal.Header closeButton>
                                <Modal.Title>Modal title</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <p>Modal body text goes here.</p>
                            </Modal.Body>
                            <Modal.Footer>
                                <button className='btn'>CLOSE</button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                </div>
            </div>
        </div>
    );
}
