import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewMenu() {
  const [ViewMenu, setViewMenu] = useState([]);

  const fetchViewMenu = async () => {
    try {
      const response = await axios.get('http://localhost:2001/viewmenu');
      setViewMenu(response.data);
    } catch (error) {
      console.error(error.message);
    }
  }

  useEffect(() => {
    fetchViewMenu();
  }, []);

 

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Menu Items</h1>
      
      <table border={1} align="center" style={{ width: 'auto', height: 'auto' }}>
          <thead>
            <tr>
              <th>ITEM NAME</th>
              <th>PRICE</th>
              <th>QUANTITY</th>
              <th>IMAGE</th>
              
            </tr>
          </thead>
          <tbody>
  {Array.isArray(ViewMenu) && ViewMenu.length > 0 ? (
    ViewMenu.map((ViewMenu, index) => (
      <tr key={index}>
        <td>{ViewMenu.fullname}</td>
        <td>{ViewMenu.gender}</td>
        <td>{ViewMenu.dateofbirth}</td>
        <td>{ViewMenu.email}</td>
        <td>{ViewMenu.location}</td>
        <td>{ViewMenu.contact}</td>
        {/* <td>
          <button onClick={() => deleteViewMenu(ViewMenu.email)} className='button'>Delete</button>
        </td> */}
      </tr>
    ))
  ) : (
    <tr>
      <td colSpan="7">Data Not Found</td>
    </tr>
  )}
</tbody>
        </table>
    </div>
  );
}
