import React, { useState } from 'react';



export default function Pizza({ pizza }) {
    const [quantity,setquantity]=useState(1)
    const [varient,setvarient]=useState('small')

    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  
    return (
        <div style={{margin:"50px"}} className='shadow-lg p-3 mb-5 bg-white rounded'>
            <h1>{pizza.name}</h1>
            <img src={pizza.image} className='img-fluid' alt={pizza.name} style={{height:"200px",width:"200px"}} />

            <div className="flex-container">
                <div className='w-100 m-1'>
                   <p>varients</p>
                   <select className='form-control'  value={varient} onChange={(e)=>{setvarient(e.target.value)}}>
                   { pizza.varients.map(
                        varient=>{
                            return <option value={varient}>{varient}</option>
                        }
                    )}
                   </select>
                </div>
                <div className='w-100 m-1'>
                 <p>Quantity</p>
                 <select className='form-control' value={quantity} onChange={(e)=>{setquantity(e.target.value)}}>
  {[...Array(10).keys()].map((_, i) => (
    <option key={i} value={i + 1}>{i + 1}</option>
  ))}
</select>
<div className="flex-container">

    <div className='m-1 w-100'>
     <h1 className='mt-1'>price:{pizza.prices[0][varient]*quantity } Rs/-</h1>
    </div>
    <div className='m-1 w-100' >
        <button className='btn'>ADD TO Cart</button>

    </div>

    <Modal.Dialog>
  <Modal.Header closeButton>
    <Modal.Title>Modal title</Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <p>Modal body text goes here.</p>
  </Modal.Body>
  <Modal.Footer>
    <Button variant="secondary">Close</Button>
    <Button variant="primary">Save changes</Button>
  </Modal.Footer>
</Modal.Dialog>
</div>

                


                 
                </div>

            </div>
        </div>
    );
}
