import {React, axios,useEffect,useState} from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Homepage from './Homepage';
import { Container, Row, Col } from 'react-bootstrap';
import foodImages from './FoodImages';
import './images.css';

import transparentVideo from '../Images/pexels_videos_2832316 (1080p).mp4'; // Import your transparent video file


export default function MainPage() {
  const navigate = useNavigate();

  const handleImageClick = (type) => {
    if (type === 'biryani') {
      navigate('/biryanis');
    } else if (type === 'pizzas') {
      navigate('/offers');
    }
    else if (type === 'Tiffins') {
      navigate('/tiffins');
    }
    else if (type === 'Non-veg Soups') {
      navigate('/nonvegsoups');
    }
  };

  const [jobseekers, setJobSeekers] = useState([]);

  const fetchJobSeekers = async () => {
    try {
      const response = await axios.get('http://localhost:2001/viewmenu');
      setJobSeekers(response.data);
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    fetchJobSeekers();
  }, []);



  return (
    <Container>
      <Row>
        <div className='image-container'>
          {foodImages.map((foodImage, index) => (
            <div key={index} onClick={() => handleImageClick(foodImage.type)}>
              <div className='circular-image'>
                {foodImage.image}
              </div>
              <h1>{foodImage.type}</h1>
            </div>
          ))}
        </div>
      </Row>
      <video
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: 'fixed',
          bottom: 0,
          right: 0,
          minWidth: '100%',
          minHeight: '100%',
          width: 'auto',
          height: 'auto',
          zIndex: -1000,
          opacity: 0.5, // Set the opacity for transparency
        }}
      >
        <source src={transparentVideo} type='video/webm' />
        Your browser does not support the video tag.
      </video>
      <div>
        <h2><b><u>YOUR MENU</u></b></h2>
        
      </div>
    </Container>
  );


}
