export const getAllPizzasReducer=(state={}, action)=>{
switch(action.type)
{
case 'GET_PIZZAS_REQUEST': return{
    ...state
}

case 'GET PIZZAS SUCCESS': return{
    pizzas :action.payload
}

case 'GET_PIZZAS_FAILED': return{
    error :action.payload
}



default: return state
}
}
