import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Viewmenu() {
  const [jobseekers, setJobSeekers] = useState([]);

  const fetchJobSeekers = async () => {
    try {
      const response = await axios.get('http://localhost:2001/viewmenu');
      setJobSeekers(response.data);
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    fetchJobSeekers();
  }, []);

  return (
    <div style={{ textAlign: 'center' }}>
      <h1><u>MENU</u></h1>
      <table border={1} align="center" width="90%" height="35%" className='nav'>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {jobseekers.map((jobseeker, index) => (
            <tr key={index}>
              <td>{jobseeker.itemname}</td>
              <td>{jobseeker.price}</td>
              <td>{jobseeker.quantity}</td>
              <td>
                {jobseeker.image && (
                 <img
                 src={`data:${jobseeker.image.contentType};base64,${jobseeker.image.data.toString('base64')}`}
                 alt=""
                 style={{ maxWidth: '100px', maxHeight: '100px' }}
               />               
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
