import React, { useState } from 'react';

export default function Pizza({ pizza }) {
    const [quantity, setQuantity] = useState(1);
    const [varient, setVarient] = useState('small');

    const handleQuantityChange = (e) => {
        setQuantity(parseInt(e.target.value));
    };

    const handleVarientChange = (e) => {
        setVarient(e.target.value);
    };

    return (
        <div className="flex-container">
            <div className="w-50">
                <h1>Price: {pizza.prices[0][varient] * quantity}</h1>
            </div>
            <div className="w-50">
                <div className="flex-container">
                    <div className="w-50">
                        <h1>{pizza.name}</h1>
                        <img src={pizza.image} className='img-fluid' alt={pizza.name} style={{ height: "200px", width: "200px" }} />
                    </div>
                    <div className="w-50">
                        <div className="flex-container">
                            <div className='w-100'>
                                <p>Varients</p>
                                <select value={varient} onChange={handleVarientChange}>
                                    {pizza.varients.map(
                                        variant => {
                                            return <option key={variant} value={variant}>{variant}</option>
                                        }
                                    )}
                                </select>
                            </div>
                            <div className='w-100'>
                                <p>Quantity</p>
                                <select value={quantity} onChange={handleQuantityChange}>
                                    {[...Array(10).keys()].map((_, i) => (
                                        <option key={i} value={i + 1}>{i + 1}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="flex-container">
                            <div>
                                <button className='btn'>ADD TO CART</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
