import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import AdminNavBar from './admin/AdminNavBar';
import SellersNavBar from './Sellermenu/Scomponents/SellersNavBar';
import MainNavBar from './main/MainNavBar';
import NavBar from './components/NavBar';

export default function App() {
   const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);
   const [isCustomerLoggedIn, setIsCustomerLoggedIn] = useState(false);
   const [isSellerLoggedIn, setIsSellerLoggedIn] = useState(false);

   useEffect(() => {
     const adminLoggedIn = localStorage.getItem('isAdminLoggedIn') === 'true';
  const customerLoggedIn = localStorage.getItem('isCustomerLoggedIn') === 'true';
     const sellerLoggedIn = localStorage.getItem('isSellerLoggedIn') === 'true';

     setIsAdminLoggedIn(adminLoggedIn);
    setIsCustomerLoggedIn(customerLoggedIn);
     setIsSellerLoggedIn(sellerLoggedIn);
   }, []);

   const onAdminLogin = () => {
     localStorage.setItem('isAdminLoggedIn', 'true');
     setIsAdminLoggedIn(true);
   };

  // const onCustomerLogin = () => {
  //   localStorage.setItem('isCustomerLoggedIn', 'true');
  //   setIsCustomerLoggedIn(true);
  // };

  // const onSellerLogin = () => {
  //   localStorage.setItem('isSellerLoggedIn', 'true');
  //   setIsSellerLoggedIn(true);
  // };

  return (
    <div className="App">

        <h3 align="center">Online Job Portal</h3>
        {isAdminLoggedIn ? (
          <AdminNavBar />
        ) : isCustomerLoggedIn ? (
          <NavBar/>
        ) : isSellerLoggedIn ? (
          <SellersNavBar />
        ) : (
          <MainNavBar
            onAdminLogin={onAdminLogin}
            onCustomerLogin={onCustomerLogin}
            onRecruiterLogin={onSellerLogin}
          />
        )}







